       {
-        "matcher": "Read|Edit|Write|NotebookEdit",
+        "matcher": "",
         "hooks": [
           {
             "type": "command",
-            "command": "mementor hook pre-tool-use"
+            "command": "entire hooks claude-code session-start"
+          }
+        ]
+      }
+    ],
+    "Stop": [
+      {
+        "matcher": "",
+        "hooks": [
+          {
+            "type": "command",
+            "command": "mementor hook stop"
+          },
+          {
+            "type": "command",
+            "command": "entire hooks claude-code stop"
           }
         ]
       }
@@ -58,6 +104,21 @@
           }
         ]
       }
+    ],
+    "UserPromptSubmit": [
+      {
+        "matcher": "",
+        "hooks": [
+          {
+            "type": "command",
+            "command": "mementor hook user-prompt-submit"
+          },
+          {
+            "type": "command",
+            "command": "entire hooks claude-code user-prompt-submit"
+          }
+        ]
+      }
     ]
   },
   "permissions": {
@@ -103,6 +164,9 @@
       "Bash(source *)",
       "Bash(test *)",
       "Bash(wc *)"
+    ],
+    "deny": [
+      "Read(./.entire/metadata/**)"
     ]
   }
 }

